"""Code Generator - Convert recorded actions to executable code"""

def generate_javascript_code(actions: list, app_package: str = None, app_activity: str = None) -> str:
    """Generate WebdriverIO/JavaScript code from actions with app configuration"""
    
    # Default app package/activity if not provided
    if not app_package:
        app_package = "com.example.app"
    if not app_activity:
        app_activity = "com.example.app.MainActivity"
    
    code_lines = [
        "// Generated by GravityQA - Automated Test",
        "// Date: " + str(__import__('datetime').datetime.now().strftime('%Y-%m-%d %H:%M')),
        "",
        "// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "// ğŸ¯ APP CONFIGURATION",
        "// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "",
        "const APP_CONFIG = {",
        f"    package: '{app_package}',",
        f"    activity: '{app_activity}',",
        "    clearAppDataBeforeLaunch: true,  // Clear app cache/data before test",
        "};",
        "",
        "const { remote } = require('webdriverio');",
        "",
        "async function runTest() {",
        "    console.log('ğŸš€ Starting test...');",
        "    console.log(`ğŸ“± App: ${{APP_CONFIG.package}}`);",
        "    console.log(`ğŸ¯ Activity: ${{APP_CONFIG.activity}}`);",
        "    ",
        "    // Connect to Appium server",
        "    const driver = await remote({",
        "        hostname: 'localhost',",
        "        port: 4723,",
        "        capabilities: {",
        "            platformName: 'Android',",
        "            'appium:automationName': 'UiAutomator2',",
        "            'appium:deviceName': 'Android Device',",
        "            ",
        "            // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "            // ğŸ“¦ APP SETTINGS",
        "            // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "            'appium:appPackage': APP_CONFIG.package,",
        "            'appium:appActivity': APP_CONFIG.activity,",
        "            ",
        "            // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "            // ğŸ§¹ CLEAR APP DATA (Fresh Start)",
        "            // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "            'appium:noReset': !APP_CONFIG.clearAppDataBeforeLaunch,",
        "            'appium:fullReset': APP_CONFIG.clearAppDataBeforeLaunch,",
        "        }",
        "    });",
        "",
        "    try {",
        "        console.log('âœ… Connected to device');",
        "        ",
        "        if (APP_CONFIG.clearAppDataBeforeLaunch) {",
        "            console.log('ğŸ§¹ App data cleared - Fresh start!');",
        "        }",
        "        ",
        "        console.log('ğŸ¬ Starting test steps...');",
        "",
    ]
    
    for i, action in enumerate(actions, 1):
        action_type = action.get('action', 'tap')
        x = action.get('x', 0)
        y = action.get('y', 0)
        
        code_lines.append(f"        // Step {i}: {action.get('description', action_type)}")
        
        if action_type == 'tap':
            element = action.get('element', {})
            selector = element.get('selector', {})
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                if strategy == 'id':
                    code_lines.append(f"        await driver.$('id={value}').click();")
                else:
                    code_lines.append(f"        await driver.$('{value}').click();")
            else:
                code_lines.append(f"        await driver.performActions([{{")
                code_lines.append(f"            type: 'pointer',")
                code_lines.append(f"            id: 'finger1',")
                code_lines.append(f"            parameters: {{ pointerType: 'touch' }},")
                code_lines.append(f"            actions: [")
                code_lines.append(f"                {{ type: 'pointerMove', duration: 0, x: {x}, y: {y} }},")
                code_lines.append(f"                {{ type: 'pointerDown', button: 0 }},")
                code_lines.append(f"                {{ type: 'pause', duration: 100 }},")
                code_lines.append(f"                {{ type: 'pointerUp', button: 0 }}")
                code_lines.append(f"            ]")
                code_lines.append(f"        }}]);")
                
        elif action_type == 'swipe':
            x2 = action.get('x2', x)
            y2 = action.get('y2', y)
            duration = action.get('duration', 500)
            
            code_lines.append(f"        await driver.performActions([{{")
            code_lines.append(f"            type: 'pointer',")
            code_lines.append(f"            id: 'finger1',")
            code_lines.append(f"            parameters: {{ pointerType: 'touch' }},")
            code_lines.append(f"            actions: [")
            code_lines.append(f"                {{ type: 'pointerMove', duration: 0, x: {x}, y: {y} }},")
            code_lines.append(f"                {{ type: 'pointerDown', button: 0 }},")
            code_lines.append(f"                {{ type: 'pause', duration: 50 }},")
            code_lines.append(f"                {{ type: 'pointerMove', duration: {duration}, x: {x2}, y: {y2} }},")
            code_lines.append(f"                {{ type: 'pointerUp', button: 0 }}")
            code_lines.append(f"            ]")
            code_lines.append(f"        }}]);")
            
        elif action_type == 'wait':
            duration = action.get('duration', 1000)
            code_lines.append(f"        await driver.pause({duration});")
        
        elif action_type == 'type_text':
            element = action.get('element', {})
            selector = element.get('selector', {})
            text = action.get('text', '')
            clear_before = action.get('clearBeforeType', False)
            press_enter = action.get('pressEnter', False)
            
            code_lines.append(f"        // Type text")
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                if strategy == 'id':
                    code_lines.append(f"        const elem{i} = await driver.$('id={value}');")
                else:
                    code_lines.append(f"        const elem{i} = await driver.$('{value}');")
                
                if clear_before:
                    code_lines.append(f"        await elem{i}.clearValue();")
                
                code_lines.append(f"        await elem{i}.setValue('{text}');")
                
                if press_enter:
                    code_lines.append(f"        await elem{i}.addValue('\\n');")
            else:
                code_lines.append(f"        // Note: TYPE_TEXT requires element locator")
        
        elif action_type == 'wait_visible' or action_type == 'wait_clickable':
            element = action.get('element', {})
            selector = element.get('selector', {})
            timeout_sec = action.get('timeoutSec', 10)
            timeout_ms = timeout_sec * 1000
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                wait_condition = 'waitForDisplayed' if action_type == 'wait_visible' else 'waitForClickable'
                
                code_lines.append(f"        // Wait for element to be {action_type.split('_')[1]}")
                
                if strategy == 'id':
                    code_lines.append(f"        await driver.$('id={value}').{wait_condition}({{ timeout: {timeout_ms} }});")
                else:
                    code_lines.append(f"        await driver.$('{value}').{wait_condition}({{ timeout: {timeout_ms} }});")
        
        elif action_type == 'assert_visible' or action_type == 'assert_text':
            element = action.get('element', {})
            selector = element.get('selector', {})
            expected_text = action.get('expectedText', '')
            timeout_sec = action.get('timeoutSec', 10)
            timeout_ms = timeout_sec * 1000
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                code_lines.append(f"        // Assert {action_type.split('_')[1]}")
                
                if action_type == 'assert_visible':
                    if strategy == 'id':
                        code_lines.append(f"        await driver.$('id={value}').waitForDisplayed({{ timeout: {timeout_ms} }});")
                    else:
                        code_lines.append(f"        await driver.$('{value}').waitForDisplayed({{ timeout: {timeout_ms} }});")
                else:  # assert_text
                    if strategy == 'id':
                        code_lines.append(f"        const actualText{i} = await driver.$('id={value}').getText();")
                    else:
                        code_lines.append(f"        const actualText{i} = await driver.$('{value}').getText();")
                    
                    code_lines.append(f"        if (actualText{i} !== '{expected_text}') {{")
                    code_lines.append(f"            throw new Error(`Expected '{expected_text}' but got '${{actualText{i}}}'`);")
                    code_lines.append(f"        }}")
        
        code_lines.append("")
    
    code_lines.extend([
        "        console.log('âœ… Test completed successfully!');",
        "",
        "    } catch (error) {",
        "        console.error('âŒ Test failed:', error);",
        "        throw error;",
        "    } finally {",
        "        await driver.deleteSession();",
        "        console.log('ğŸ‘‹ Session closed');",
        "    }",
        "}",
        "",
        "// Run the test",
        "runTest().catch(console.error);",
    ])
    
    return '\n'.join(code_lines)


def generate_python_code(actions: list, app_package: str = None, app_activity: str = None) -> str:
    """Generate Python/Appium code from actions with app configuration"""
    
    if not app_package:
        app_package = "com.example.app"
    if not app_activity:
        app_activity = "com.example.app.MainActivity"
    
    code_lines = [
        "# Generated by GravityQA - Automated Test",
        "# Date: " + str(__import__('datetime').datetime.now().strftime('%Y-%m-%d %H:%M')),
        "",
        "# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "# ğŸ¯ APP CONFIGURATION",
        "# â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "",
        "APP_CONFIG = {",
        f"    'package': '{app_package}',",
        f"    'activity': '{app_activity}',",
        "    'clear_app_data_before_launch': True,  # Clear app cache/data before test",
        "}",
        "",
        "from appium import webdriver",
        "from appium.webdriver.common.appiumby import AppiumBy",
        "from selenium.webdriver.common.actions import interaction",
        "from selenium.webdriver.common.actions.action_builder import ActionBuilder",
        "from selenium.webdriver.common.actions.pointer_input import PointerInput",
        "import time",
        "",
        "def run_test():",
        "    print('ğŸš€ Starting test...')",
        "    print(f\"ğŸ“± App: {APP_CONFIG['package']}\")",
        "    print(f\"ğŸ¯ Activity: {APP_CONFIG['activity']}\")",
        "    ",
        "    # Appium capabilities",
        "    caps = {",
        "        'platformName': 'Android',",
        "        'appium:automationName': 'UiAutomator2',",
        "        'appium:deviceName': 'Android Device',",
        "        ",
        "        # â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "        # ğŸ“¦ APP SETTINGS",
        "        # â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "        'appium:appPackage': APP_CONFIG['package'],",
        "        'appium:appActivity': APP_CONFIG['activity'],",
        "        ",
        "        # â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "        # ğŸ§¹ CLEAR APP DATA (Fresh Start)",
        "        # â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•",
        "        'appium:noReset': not APP_CONFIG['clear_app_data_before_launch'],",
        "        'appium:fullReset': APP_CONFIG['clear_app_data_before_launch'],",
        "    }",
        "    ",
        "    driver = webdriver.Remote(",
        "        'http://localhost:4723',",
        "        caps",
        "    )",
        "",
        "    try:",
        "        print('âœ… Connected to device')",
        "        ",
        "        if APP_CONFIG['clear_app_data_before_launch']:",
        "            print('ğŸ§¹ App data cleared - Fresh start!')",
        "        ",
        "        print('ğŸ¬ Starting test steps...')",
        "",
    ]
    
    for i, action in enumerate(actions, 1):
        action_type = action.get('action', 'tap')
        x = action.get('x', 0)
        y = action.get('y', 0)
        
        code_lines.append(f"        # Step {i}: {action.get('description', action_type)}")
        
        if action_type == 'tap':
            element = action.get('element', {})
            selector = element.get('selector', {})
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                if strategy == 'id':
                    code_lines.append(f"        driver.find_element(AppiumBy.ID, '{value}').click()")
                else:
                    code_lines.append(f"        driver.find_element(AppiumBy.XPATH, '{value}').click()")
            else:
                code_lines.append(f"        actions = ActionBuilder(driver, mouse=PointerInput(interaction.POINTER_TOUCH, 'touch'))")
                code_lines.append(f"        actions.pointer_action.move_to_location({x}, {y})")
                code_lines.append(f"        actions.pointer_action.pointer_down()")
                code_lines.append(f"        actions.pointer_action.pause(0.1)")
                code_lines.append(f"        actions.pointer_action.pointer_up()")
                code_lines.append(f"        actions.perform()")
                
        elif action_type == 'swipe':
            x2 = action.get('x2', x)
            y2 = action.get('y2', y)
            duration = action.get('duration', 500)
            
            code_lines.append(f"        actions = ActionBuilder(driver, mouse=PointerInput(interaction.POINTER_TOUCH, 'touch'))")
            code_lines.append(f"        actions.pointer_action.move_to_location({x}, {y})")
            code_lines.append(f"        actions.pointer_action.pointer_down()")
            code_lines.append(f"        actions.pointer_action.pause(0.05)")
            code_lines.append(f"        actions.pointer_action.move_to_location({x2}, {y2}, duration={duration / 1000})")
            code_lines.append(f"        actions.pointer_action.pointer_up()")
            code_lines.append(f"        actions.perform()")
            
        elif action_type == 'wait':
            duration = action.get('duration', 1000)
            code_lines.append(f"        time.sleep({duration / 1000})")
        
        elif action_type == 'type_text':
            element = action.get('element', {})
            selector = element.get('selector', {})
            text = action.get('text', '')
            clear_before = action.get('clearBeforeType', False)
            press_enter = action.get('pressEnter', False)
            
            code_lines.append(f"        # Type text")
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                if strategy == 'id':
                    code_lines.append(f"        elem{i} = driver.find_element(AppiumBy.ID, '{value}')")
                else:
                    code_lines.append(f"        elem{i} = driver.find_element(AppiumBy.XPATH, '{value}')")
                
                if clear_before:
                    code_lines.append(f"        elem{i}.clear()")
                
                code_lines.append(f"        elem{i}.send_keys('{text}')")
                
                if press_enter:
                    code_lines.append(f"        elem{i}.send_keys('\\n')")
            else:
                code_lines.append(f"        # Note: TYPE_TEXT requires element locator")
        
        elif action_type == 'wait_visible' or action_type == 'wait_clickable':
            element = action.get('element', {})
            selector = element.get('selector', {})
            timeout_sec = action.get('timeoutSec', 10)
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                code_lines.append(f"        # Wait for element to be {action_type.split('_')[1]}")
                code_lines.append(f"        from selenium.webdriver.support.ui import WebDriverWait")
                code_lines.append(f"        from selenium.webdriver.support import expected_conditions as EC")
                
                if action_type == 'wait_visible':
                    if strategy == 'id':
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.visibility_of_element_located((AppiumBy.ID, '{value}')))")
                    else:
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.visibility_of_element_located((AppiumBy.XPATH, '{value}')))")
                else:  # wait_clickable
                    if strategy == 'id':
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.element_to_be_clickable((AppiumBy.ID, '{value}')))")
                    else:
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.element_to_be_clickable((AppiumBy.XPATH, '{value}')))")
        
        elif action_type == 'assert_visible' or action_type == 'assert_text':
            element = action.get('element', {})
            selector = element.get('selector', {})
            expected_text = action.get('expectedText', '')
            timeout_sec = action.get('timeoutSec', 10)
            
            if selector and selector.get('value'):
                strategy = selector.get('strategy', 'xpath')
                value = selector.get('value', '')
                
                code_lines.append(f"        # Assert {action_type.split('_')[1]}")
                
                if action_type == 'assert_visible':
                    code_lines.append(f"        from selenium.webdriver.support.ui import WebDriverWait")
                    code_lines.append(f"        from selenium.webdriver.support import expected_conditions as EC")
                    
                    if strategy == 'id':
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.visibility_of_element_located((AppiumBy.ID, '{value}')))")
                    else:
                        code_lines.append(f"        WebDriverWait(driver, {timeout_sec}).until(EC.visibility_of_element_located((AppiumBy.XPATH, '{value}')))")
                else:  # assert_text
                    if strategy == 'id':
                        code_lines.append(f"        actual_text{i} = driver.find_element(AppiumBy.ID, '{value}').text")
                    else:
                        code_lines.append(f"        actual_text{i} = driver.find_element(AppiumBy.XPATH, '{value}').text")
                    
                    code_lines.append(f"        if actual_text{i} != '{expected_text}':")
                    code_lines.append(f"            raise AssertionError(f\"Expected '{expected_text}' but got '{{actual_text{i}}}'\")")
        
        code_lines.append("")
    
    code_lines.extend([
        "        print('âœ… Test completed successfully!')",
        "",
        "    except Exception as e:",
        "        print(f'âŒ Test failed: {e}')",
        "        raise",
        "    finally:",
        "        driver.quit()",
        "        print('ğŸ‘‹ Session closed')",
        "",
        "",
        "if __name__ == '__main__':",
        "    run_test()",
    ])
    
    return '\n'.join(code_lines)
